import 'package:flutter/cupertino.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:freezed_annotation/freezed_annotation.dart';import 'dart:async';import 'package:news_app/models/news/news.dart';import 'package:news_app/repositories/news_repository.dart';import 'package:news_app/services/network/error_handler.dart';part 'business_news_state.dart';part 'business_news_cubit.freezed.dart';/// The BusinessNewsCubitclass BusinessNewsCubit extends Cubit<BusinessNewsState> {  final NewsRepository _newsRepository;  /// Create a new instance of [BusinessNewsCubit].  BusinessNewsCubit(this._newsRepository) : super(const BusinessNewsState.initial([]));  /// Method used to perform the [getNews] action  Future<FutureOr<void>> getNews({required BuildContext context, required String category}) async {    emit(const BusinessNewsState.loading());    final response = await _newsRepository.newsByCategory(context: context, category: category);    if (response.data != null) {      emit(BusinessNewsState.data(response.data));    } else {      ErrorHandler e = response.getException;      emit(BusinessNewsState.error(e.getErrorMessage()));    }  }}