import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:news_app/shared/shared.dart';class ErrorCubit extends Cubit<bool> {  ErrorCubit() : super(false);  Future<void> showError(Map<String, dynamic> error, {required BuildContext context}) async {    if (!state && context.mounted) {      emit(true);      showModal(        context,        height: 300.h,        child: error["statusCode"] == K.noInternetConnection            ? NoInternetAnimation(error: error["message"] ?? "")            : _buildErrorWidget(error),      ).then((_) => emit(false));    } else {      emit(false);    }  }  Widget _buildErrorWidget(Map<String, dynamic> error) {    return Column(      children: <Widget>[        Image.asset(          "assets/icons/error.png",          height: 50.h,          color: Colors.red,        ),        20.vSpacer,        Text(          error["error"] ?? "",          textAlign: TextAlign.center,          style: TextStyle(            fontSize: 20.text,            fontWeight: FontWeight.w600,            height: 32.h / 20.text,          ),        ),        24.vSpacer,        Padding(          padding: EdgeInsets.symmetric(horizontal: 20.w),          child: Text(            error["message"] ?? "",            textAlign: TextAlign.center,            style: TextStyle(              fontSize: 12.text,              fontWeight: FontWeight.w500,              color: Colors.grey,            ),          ),        ),      ],    );  }}