import 'package:flutter_bloc/flutter_bloc.dart';import 'package:news_app/blocs/news/news_bloc.dart';import 'package:news_app/blocs/trending_news/trending_news_bloc.dart';import 'package:news_app/cubits/business_news/business_news_cubit.dart';import 'package:news_app/cubits/entertainment_news/entertainment_news_cubit.dart';import 'package:news_app/cubits/health_news/health_news_cubit.dart';import 'package:news_app/cubits/reading_list/reading_list_cubit.dart';import 'package:news_app/cubits/science_news/science_news_cubit.dart';import 'package:news_app/cubits/sport_news/sport_news_cubit.dart';import 'package:news_app/cubits/technology_news/technology_news_cubit.dart';import 'package:provider/single_child_widget.dart';List<SingleChildWidget> newsProviders = [  BlocProvider<NewsBloc>(    create: (context) => NewsBloc(      newsRepository: context.read(),      localRepository: context.read(),    ),  ),  BlocProvider<TrendingNewsBloc>(      create: (context) => TrendingNewsBloc(            newsRepository: context.read(),          )),  BlocProvider<ReadingListCubit>(    create: (context) => ReadingListCubit(),  ),  BlocProvider<BusinessNewsCubit>(    create: (context) => BusinessNewsCubit(context.read()),  ),  BlocProvider<EntertainmentNewsCubit>(    create: (context) => EntertainmentNewsCubit(context.read()),  ),  BlocProvider<HealthNewsCubit>(    create: (context) => HealthNewsCubit(context.read()),  ),  BlocProvider<ScienceNewsCubit>(    create: (context) => ScienceNewsCubit(context.read()),  ),  BlocProvider<SportNewsCubit>(    create: (context) => SportNewsCubit(context.read()),  ),  BlocProvider<TechnologyNewsCubit>(    create: (context) => TechnologyNewsCubit(context.read()),  ),];