import 'package:dio/dio.dart';import 'package:flutter/material.dart';import 'package:news_app/services/network/error_handler.dart';import 'package:news_app/shared/size_config/extensions.dart';class K {  const K._();  static Duration networkTimeout = const Duration(seconds: 30);  static const baseUrl = "https://newsapi.org/v2";  static const requestCanceled = 1001;  static const connectionTimeout = 1002;  static const receiveTimeout = 1003;  static const unknownError = 1004;  static const noInternetConnection = 1007;  static const unexpectedCases = 1008;  static List<String> NEWS_TABBAR_ICONS = [    "",    "assets/icons/Pie Chart 2.png",    "assets/icons/Emoji Funny Square.png",    "assets/icons/Pill.png",    "assets/icons/Star Rings.png",    "assets/icons/Football.png",    "assets/icons/Programming.png",  ];  static List<String> NEWS_TABBAR_TEXTS = [    "ðŸ”¥ All",    "business",    "entertainment",    "health",    "science",    "sports",    "technology",  ];  static void handleError(BuildContext context, {required e, data}) {    String message = "";    String resMessage = "";    int? statusCode;    if (e is DioException) {      ErrorHandler errorHandler = ErrorHandler.dioException(error: e);      message = errorHandler.getErrorMessage();      statusCode = errorHandler.getStatusCode();      if (e.response != null && e.response!.data!["message"] != null) {        if (statusCode != null && statusCode != K.noInternetConnection) {          resMessage = e.response!.data!["message"];        } else {          resMessage = "Check your internet connection and try again! ";        }      }    } else {      ErrorHandler errorHandler = ErrorHandler.otherException();      message = errorHandler.getErrorMessage();    }    Map<String, dynamic> errorData = {};    errorData = {      "error": message,      "message": resMessage,      "statusCode": statusCode,      "loginData": data,    };    if (context.mounted) showErrorModal(context, errorData);  }}showErrorModal(context, Map<String, dynamic> error) {  showModal(context, height: 320.relHeight, child: StatefulBuilder(builder: (context, setState) {    return Column(      children: <Widget>[        Image.asset(          error["statusCode"] == K.noInternetConnection              ? "assets/icons/Wi-Fi Router.png"              : "assets/icons/error.png",          height: 50.relHeight,          color: Colors.red,        ),        20.vSpacer,        Text(          error["error"] ?? "",          textAlign: TextAlign.center,          style: TextStyle(            fontSize: 24.text,            fontWeight: FontWeight.w600,            height: 32.relHeight / 24.text,          ),        ),        20.vSpacer,        Padding(          padding: EdgeInsets.symmetric(horizontal: 20.relWidth),          child: Text(            error["message"] ?? "",            textAlign: TextAlign.center,            style: TextStyle(              fontSize: 12.text,              fontWeight: FontWeight.w500,              color: Colors.grey,            ),          ),        ),      ],    );  }));}showModal(context, {height, isDismissible = false, required child, bool useRootNavigator = false}) {  showModalBottomSheet(    context: context,    backgroundColor: Colors.white,    useRootNavigator: useRootNavigator,    barrierColor: const Color(0xFF9CA3AF).withOpacity(0.2),    // isDismissible: isDismissible,    isScrollControlled: true,    //elevates modal bottom screen    elevation: 10,    shape: RoundedRectangleBorder(      borderRadius: BorderRadius.only(          topRight: Radius.circular(            32.relHeight,          ),          topLeft: Radius.circular(32.relHeight)),    ),    builder: (BuildContext context) {      return Padding(        padding: EdgeInsets.only(bottom: MediaQuery.of(context).viewInsets.bottom),        child: Container(          height: height,          decoration: BoxDecoration(            borderRadius: BorderRadius.vertical(top: Radius.circular(32.relHeight)),            color: Colors.white,          ),          padding: EdgeInsets.symmetric(horizontal: 24.relWidth),          child: Center(            child: Column(              children: [                12.vSpacer,                Container(                  height: 5.relHeight,                  width: 56.relWidth,                  decoration: BoxDecoration(                    borderRadius: BorderRadius.all(                      Radius.circular(32.relHeight),                    ),                    color: Colors.grey,                  ),                ),                24.vSpacer,                child              ],            ),          ),        ),      );    },  );}