import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:news_app/shared/shared.dart';class AnimatedArrow extends StatefulWidget {  const AnimatedArrow({super.key});  @override  AnimatedArrowState createState() => AnimatedArrowState();}class AnimatedArrowState extends State<AnimatedArrow> with SingleTickerProviderStateMixin {  late AnimationController _animationController;  late Animation<double> _arrowAnimation;  @override  void initState() {    super.initState();    _animationController = AnimationController(      vsync: this,      duration: const Duration(milliseconds: 800),    )..repeat(reverse: true);    _arrowAnimation = Tween<double>(begin: 0, end: 10).animate(      CurvedAnimation(parent: _animationController, curve: Curves.easeInOut),    );  }  @override  void dispose() {    _animationController.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    return AnimatedBuilder(      animation: _arrowAnimation,      builder: (context, child) {        return Transform.translate(            offset: Offset(0, -_arrowAnimation.value),            child: Column(              children: [                Icon(                  CupertinoIcons.chevron_down,                  size: 16.h,                  color: Colors.white38,                ),                4.vSpacer,                Text(                  "Drag down to exit",                  textAlign: TextAlign.center,                  style: TextStyle(                    fontSize: 10.text,                    fontWeight: FontWeight.w500,                    color: Colors.white38,                  ),                )              ],            ));      },    );  }}