import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:news_app/cubits/business_news/business_news_cubit.dart';import 'package:news_app/cubits/entertainment_news/entertainment_news_cubit.dart';import 'package:news_app/cubits/health_news/health_news_cubit.dart';import 'package:news_app/cubits/science_news/science_news_cubit.dart';import 'package:news_app/shared/widgets/no-data.dart';import '../../shared.dart';class ScienceCategory extends StatelessWidget {  const ScienceCategory({super.key});  @override  Widget build(BuildContext context) {    return BlocBuilder<ScienceNewsCubit, ScienceNewsState>(      builder: (context, state) => _mapToState(state, context),    );  }  Widget _mapToState(ScienceNewsState state, context) {    return state.map(      initial: (value) => const SizedBox.shrink(),      loading: (value) => const LoadingWidget(),      error: (value) => const NoDataAnimation(),      data: (value) => _buildNewsView(value, context),    );  }  Widget _buildNewsView(DataScienceNewsState value, context) {    final news =        value.news.where((e) => e.title != "[Removed]" && !e.title!.contains("GMT")).toList();    return Column(      crossAxisAlignment: CrossAxisAlignment.start,      children: List.generate(        news.length,        (index) {          final _ = news[index];          return NewsListCard(            news: _,            context: context,          );        },      ),    );  }}