import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:news_app/cubits/business_news/business_news_cubit.dart';import 'package:news_app/cubits/entertainment_news/entertainment_news_cubit.dart';import 'package:news_app/cubits/health_news/health_news_cubit.dart';import 'package:news_app/cubits/science_news/science_news_cubit.dart';import 'package:news_app/cubits/sport_news/sport_news_cubit.dart';import '../../shared.dart';class SportCategory extends StatelessWidget {  const SportCategory({super.key});  @override  Widget build(BuildContext context) {    return BlocBuilder<SportNewsCubit, SportNewsState>(      builder: (context, state) => _mapToState(state, context),    );  }  Widget _mapToState(SportNewsState state, context) {    return state.map(        initial: (value) => const Center(              child: SizedBox.shrink(),            ),        loading: (value) => const Column(              mainAxisAlignment: MainAxisAlignment.center,              children: [                CircularProgressIndicator.adaptive(),              ],            ),        data: (value) => _buildNewsView(value, context),        error: (value) => const SizedBox.shrink());  }  Widget _buildNewsView(DataSportNewsState value, context) {    final news =        value.news.where((e) => e.title != "[Removed]" && !e.title!.contains("GMT")).toList();    return Column(      crossAxisAlignment: CrossAxisAlignment.start,      children: List.generate(        news.length,        (index) {          final _ = news[index];          return NewsListCard(            news: _,            context: context,          );        },      ),    );  }}