import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:news_app/cubits/business_news/business_news_cubit.dart';import 'package:news_app/cubits/technology_news/technology_news_cubit.dart';import 'package:news_app/shared/widgets/no-data.dart';import '../../shared.dart';class TechnologyCategory extends StatelessWidget {  const TechnologyCategory({super.key});  @override  Widget build(BuildContext context) {    return BlocBuilder<TechnologyNewsCubit, TechnologyNewsState>(      builder: (context, state) => _mapToState(state, context),    );  }  Widget _mapToState(TechnologyNewsState state, context) {    return state.map(      initial: (value) => const SizedBox.shrink(),      loading: (value) => const LoadingWidget(),      error: (value) => const NoDataAnimation(),      data: (value) => _buildNewsView(value, context),    );  }  Widget _buildNewsView(DataTechnologyNewsState value, context) {    final news =        value.news.where((e) => e.title != "[Removed]" && !e.title!.contains("GMT")).toList();    return Column(      crossAxisAlignment: CrossAxisAlignment.start,      children: List.generate(        news.length,        (index) {          final _ = news[index];          return NewsListCard(            news: _,            context: context,          );        },      ),    );  }}