import 'package:flutter/material.dart';import 'package:news_app/shared/size_config/extensions.dart';class TabWidget extends StatelessWidget {  final int index;  final int categoryIndex;  final List<String> tabs;  final List<GlobalKey> keys;  final List<String> icons;  final Function(int) onTap;  const TabWidget({    super.key,    required this.index,    required this.categoryIndex,    required this.tabs,    required this.keys,    required this.icons,    required this.onTap,  });  @override  Widget build(BuildContext context) {    return GestureDetector(      onTap: () => onTap(index),      child: Tab(        key: keys[index],        child: AnimatedContainer(          height: 40.h,          padding: EdgeInsets.symmetric(vertical: 8.h, horizontal: 12.w),          alignment: Alignment.center,          duration: const Duration(milliseconds: 200),          curve: Curves.slowMiddle,          margin: EdgeInsets.only(              right: index < tabs.length - 1 ? 16.w : 20.w, left: index == 0 ? 20.w : 0),          decoration: BoxDecoration(            borderRadius: BorderRadius.circular(4.w),            border: Border.all(              color: categoryIndex == index ? Colors.transparent : Colors.black12,            ),            color: categoryIndex == index ? Colors.black : Colors.white,          ),          child: Row(            children: [              if (index > 0) ...[                Image.asset(                  icons[index],                  height: 24.h,                  color: categoryIndex == index ? Colors.white : Colors.black,                ),                4.hSpacer,              ],              Text(                tabs[index],                style: TextStyle(                  color: categoryIndex == index ? Colors.white : Colors.black,                  fontWeight: FontWeight.w400,                  fontSize: 14.text,                ),              ),            ],          ),        ),      ),    );  }}