// ignore_for_file: file_namesimport 'package:flutter/material.dart';import 'package:news_app/shared/size_config/extensions.dart';Widget buildDefaultAppBar(BuildContext context,    {Widget? rightWidget,    Widget? title,    double? height,    EdgeInsets? padding,    EdgeInsets? margin,    buttonColor,    bgColor,    backButtonColor,    Widget? leftWidget}) {  return Container(    height: height,    padding: padding,    margin: margin,    child: Row(      mainAxisAlignment: MainAxisAlignment.spaceBetween,      crossAxisAlignment: CrossAxisAlignment.center,      children: [        leftWidget ??            WynkBackButton(              buttonColor: backButtonColor,            ),        title ?? 40.hSpacer,        rightWidget ?? 40.hSpacer,      ],    ),  );}class WynkBackButton extends StatelessWidget {  final dynamic isClose;  final dynamic onTap;  final dynamic bgColor;  final dynamic height;  final dynamic width;  final dynamic size;  final dynamic buttonColor;  const WynkBackButton(      {super.key,      this.isClose = false,      this.onTap,      this.bgColor,      this.height,      this.width,      this.size,      this.buttonColor});  @override  Widget build(BuildContext context) {    return InkWell(        onTap: onTap ??            () {              Navigator.pop(context);            },        child: Container(          height: size,          width: size,          padding: EdgeInsets.symmetric(vertical: 8.relHeight, horizontal: 8.relWidth),          decoration: BoxDecoration(shape: BoxShape.circle, color: bgColor),          child: isClose              ? Icon(                  Icons.close_rounded,                  size: height ?? 24.relHeight,                  color: buttonColor,                )              : Icon(                  Icons.chevron_left,                  size: height ?? 24.relHeight,                  color: buttonColor,                ),        ));  }}