import 'package:flutter/material.dart';import 'package:news_app/shared/shared.dart';Future<void> showModal(context,    {height, isDismissible = false, required child, bool useRootNavigator = false}) async {  await showModalBottomSheet(    context: context,    backgroundColor: Colors.white,    useRootNavigator: useRootNavigator,    barrierColor: const Color(0xFF9CA3AF).withOpacity(0.2),    isDismissible: isDismissible,    isScrollControlled: true,    elevation: 10,    shape: RoundedRectangleBorder(      borderRadius: BorderRadius.only(          topRight: Radius.circular(            32.h,          ),          topLeft: Radius.circular(32.h)),    ),    builder: (BuildContext context) {      return Padding(        padding: EdgeInsets.only(bottom: MediaQuery.of(context).viewInsets.bottom),        child: Container(          height: height,          decoration: BoxDecoration(            borderRadius: BorderRadius.vertical(top: Radius.circular(32.h)),            color: Colors.white,          ),          padding: EdgeInsets.symmetric(horizontal: 24.w),          child: Center(            child: Column(              children: [                12.vSpacer,                Container(                  height: 5.h,                  width: 56.w,                  decoration: BoxDecoration(                    borderRadius: BorderRadius.all(                      Radius.circular(32.h),                    ),                    color: Colors.grey,                  ),                ),                24.vSpacer,                child              ],            ),          ),        ),      );    },  );}