import 'package:flutter/material.dart';import 'package:news_app/shared/size_config/extensions.dart';import 'package:news_app/shared/widgets/sliver.dart';class NewsCarousel extends StatelessWidget {  const NewsCarousel({    super.key,    required this.controller,    required this.currentPage,  });  final PageController controller;  final int currentPage;  @override  Widget build(BuildContext context) {    return SliverPersistentHeader(      pinned: true,      delegate: MySliverAppBar(        expandedHeight: 250.relHeight,        controller: controller,        currentPage: currentPage,      ),    );  }}