import 'package:flutter/material.dart';import 'package:lottie/lottie.dart';import 'package:news_app/shared/size_config/extensions.dart';class NoDataAnimation extends StatefulWidget {  const NoDataAnimation({super.key, this.tryAgain});  final VoidCallback? tryAgain;  @override  State<NoDataAnimation> createState() => _NoDataAnimationState();}class _NoDataAnimationState extends State<NoDataAnimation> with TickerProviderStateMixin {  late final AnimationController _controller;  @override  void initState() {    super.initState();    _controller = AnimationController(vsync: this)..repeat(period: const Duration(seconds: 8));  }  @override  void dispose() {    _controller.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    return Container(      padding: EdgeInsets.symmetric(horizontal: 20.w),      child: Column(        mainAxisAlignment: MainAxisAlignment.center,        children: [          Lottie.asset(            "assets/animations/no-data.json",            controller: _controller,            height: 150.h,            onLoaded: (composition) {              _controller.duration = composition.duration;            },          ),          Text(            "Oops! nothing found",            textAlign: TextAlign.center,            style: TextStyle(              fontSize: 14.text,              fontWeight: FontWeight.w500,              color: Colors.black,            ),          ),          8.vSpacer,          TextButton(            style: ButtonStyle(              backgroundColor: const WidgetStatePropertyAll(Colors.white),              shape: WidgetStatePropertyAll(RoundedRectangleBorder(                  borderRadius: BorderRadius.circular(8.h), side: BorderSide(color: Colors.black))),              fixedSize: WidgetStatePropertyAll(Size(double.maxFinite, 40.h)),            ),            onPressed: widget.tryAgain,            child: Text(              "Try Again",              textAlign: TextAlign.center,              style: TextStyle(                fontSize: 12.text,                fontWeight: FontWeight.w500,                color: Colors.black,              ),            ),          ),          const Spacer(flex: 3)        ],      ),    );  }}