import 'package:flutter/material.dart';import 'package:lottie/lottie.dart';import 'package:news_app/shared/size_config/extensions.dart';class NoInternetAnimation extends StatefulWidget {  final String error;  const NoInternetAnimation({super.key, required this.error});  @override  State<NoInternetAnimation> createState() => _NoInternetAnimationState();}class _NoInternetAnimationState extends State<NoInternetAnimation> with TickerProviderStateMixin {  late final AnimationController _controller;  @override  void initState() {    super.initState();    _controller = AnimationController(vsync: this)..repeat(period: const Duration(seconds: 8));  }  @override  void dispose() {    _controller.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    return Column(      mainAxisAlignment: MainAxisAlignment.center,      children: [        Lottie.asset(          "assets/animations/no-internet.json",          controller: _controller,          height: 150.h,          onLoaded: (composition) {            _controller.duration = composition.duration;          },        ),        16.vSpacer,        Padding(          padding: EdgeInsets.symmetric(horizontal: 20.w),          child: Text(            widget.error,            textAlign: TextAlign.center,            style: TextStyle(              fontSize: 12.text,              fontWeight: FontWeight.w500,              color: Colors.black,            ),          ),        ),      ],    );  }}