import 'package:flutter/material.dart';import 'package:news_app/shared/size_config/extensions.dart';class PageIndicator extends StatelessWidget {  final int currentPage;  final int pageCount;  final double? spacing;  final Color? activeIndicatorColor;  final Color? inactiveIndicatorColor;  const PageIndicator({    super.key,    required this.currentPage,    required this.pageCount,    this.spacing,    this.activeIndicatorColor,    this.inactiveIndicatorColor,  });  @override  Widget build(BuildContext context) {    return Row(      mainAxisAlignment: MainAxisAlignment.center,      children: List.generate(        pageCount,        (index) => AnimatedContainer(          duration: const Duration(milliseconds: 500),          margin: EdgeInsets.only(right: index < pageCount - 1 ? spacing ?? 4.relWidth : 0),          width: currentPage == index ? 7.relWidth : 5.relWidth,          height: currentPage == index ? 7.relHeight : 5.relHeight,          decoration: BoxDecoration(            shape: BoxShape.circle,            color: currentPage == index                ? (activeIndicatorColor ?? Colors.blue)                : (inactiveIndicatorColor ?? Colors.blue.withOpacity(.15)),          ),        ),      ),    );  }}