import 'package:flutter/material.dart';import 'package:news_app/shared/size_config/extensions.dart';import 'package:news_app/shared/widgets/sliver.dart';class NewsSearchBar extends StatelessWidget {  const NewsSearchBar({    super.key,  });  @override  Widget build(BuildContext context) {    return SliverPersistentHeader(        pinned: true,        delegate: CustomSliver(          expandedHeight: 56.h,          child: Container(            color: Colors.white,            height: 56.h,            alignment: Alignment.center,            padding: EdgeInsets.symmetric(horizontal: 20.w, vertical: 8.h),            child: SizedBox(              height: 40.h,              child: TextFormField(                decoration: InputDecoration(                  prefixIcon: Container(                    padding: EdgeInsets.symmetric(vertical: 8.h, horizontal: 8.w),                    child: Image.asset(                      "assets/icons/Magnifer.png",                      height: 24.h,                      color: Colors.grey,                    ),                  ),                  hintText: "eg. Apple, Bitcoin...",                  hintStyle: TextStyle(                    fontSize: 12.text,                    color: Colors.black,                    height: 20.h / 12.text,                  ),                  enabledBorder: OutlineInputBorder(                    borderRadius: BorderRadius.circular(8.w),                    borderSide: BorderSide(                      width: 0.5.w,                      color: Colors.grey.withOpacity(0.5),                    ),                  ),                  focusedBorder: OutlineInputBorder(                    borderRadius: BorderRadius.circular(8.w),                    borderSide: BorderSide(                      width: 0.5.w,                      color: Colors.black,                    ),                  ),                ),              ),            ),          ),        ));  }}