import 'package:flutter/material.dart';import 'package:news_app/shared/size_config/extensions.dart';import 'package:news_app/shared/widgets/sliver.dart';class NewsSearchBar extends StatelessWidget {  const NewsSearchBar({    super.key,  });  @override  Widget build(BuildContext context) {    return SliverPersistentHeader(        pinned: true,        delegate: CustomSliver(          expandedHeight: 98.relHeight,          child: Container(            color: Colors.white,            height: 98.relHeight,            alignment: Alignment.center,            padding: EdgeInsets.symmetric(horizontal: 20.relWidth),            child: SizedBox(              height: 50.relHeight,              child: TextFormField(                decoration: InputDecoration(                    prefixIcon: Container(                      padding: EdgeInsets.symmetric(vertical: 8.relHeight, horizontal: 8.relWidth),                      child: Image.asset(                        "assets/icons/Magnifer.png",                        height: 24.relHeight,                        color: Colors.grey,                      ),                    ),                    hintText: "eg. Apple, Bitcoin...",                    border: OutlineInputBorder(                      borderRadius: BorderRadius.circular(8.relWidth),                      borderSide: BorderSide(                        color: Colors.grey.withOpacity(0.5),                      ),                    )),              ),            ),          ),        ));  }}